import React,{Component, useState } from 'react';
import{connect} from 'react-redux'
import SideBarSubMenu from './SideBarSubMenu';
import './SideBar.scss'
import { Collapse, CardBody, Card } from 'reactstrap';


const SideBar =(props)=> {
  
    const [isOpen, setIsOpen] = useState(false);

    const toggle = (event) => setIsOpen(!isOpen);
  
    
        return (
            <div className="side-bar">
                {
                    props.SideBarTitles.map((el)=>
                    <div className="module">
                        <div className="menu-title" onClick={this.toggle()} style={{ marginBottom: '1rem' }} >
                           
                                {el.icon}
                                <h2>{el.name}</h2>
                        </div>
                        <Collapse isOpen={isOpen}>
                            <Card>
                                <CardBody>
                                 {el.submenu && el.submenu.map(el=><SideBarSubMenu  object={el.name}/>)}
                                </CardBody>
                            </Card>
                        </Collapse>
                    </div>
                    )
                }    
            </div>
          )
    }
 

const mapStateToProps = (state) =>{
    return{
        SideBarTitles : state.SideBarTitles
    }
}
const ConnectedSideBar = connect(mapStateToProps)(SideBar)

export default ConnectedSideBar;
